mapper.update:
UPDATE PRODUCT
SET DELETED_AT = SYSDATE
WHERE PRODUCT_ID = #{productId}

UPDATE ORDERS SET
                  ORDER_STATUS = #{orderStatus}
WHERE
    ORDER_ID = #{orderId}

UPDATE REVIEW
SET DELETED_AT = SYSDATE
WHERE REVIEW_ID = #{reviewId}

UPDATE REVIEW
SET IS_SELECTED = #{isSelected}
WHERE REVIEW_ID = #{reviewId}
