mapper.select:
SELECT POINT
FROM USER_TABLE
WHERE USER_ID = #{user_id}

mapper.update:
UPDATE USER_TABLE
SET
    point = point - #{pointsToDeduct}
WHERE
    user_id = #{user_id}
    AND point >= #{pointsToDeduct}

UPDATE PRODUCT SET
    stock = stock - #{quantity}
WHERE
    product_id = #{product_id}
  AND stock >= #{quantity}
