mapper.insert:
INSERT INTO qna (
qna_id, product_id, user_id, title, content, created_at
) VALUES (
SEQ_QNA.NEXTVAL, #{product_id}, #{user_id}, #{title}, #{content}, SYSDATE
)

mapper.update:
UPDATE qna
SET title = #{title},
content = #{content}
WHERE qna_id = #{qna_id}
AND user_id = #{user_id}

UPDATE qna
SET deleted_at = SYSDATE
WHERE qna_id = #{qna_id}
AND user_id = #{user_id}
