mapper.insert:
INSERT INTO review_comment (
review_comment_id, review_id, user_id, content, created_at
) VALUES (
seq_review_comment.NEXTVAL, #{review_id}, #{user_id}, #{content}, SYSDATE
)

mapper.select:
SELECT is_like
FROM review_like
WHERE review_id = #{review_id} AND user_id = #{user_id}

mapper.update:
UPDATE review
SET


        like_count = like_count + #{amount}


        dislike_count = dislike_count + #{amount}


WHERE review_id = #{review_id}

mapper.insert:
INSERT INTO review_report (
report_id, review_id, user_id, reason, description, status, created_at
) VALUES (
