mapper.insert:
INSERT INTO USER_TABLE (
    ID, PASSWORD, NAME, EMAIL, NICKNAME, PHONE_NUMBER, BAUMANN_ID, ROLE
) VALUES (
             #{id}, #{password}, #{name}, #{email}, #{nickname}, #{phone_number}, #{baumann_id}, #{role}
         )

mapper.update:
UPDATE USER_TABLE
SET
    PASSWORD = #{newEncodedPassword}
WHERE
    ID = #{id}

mapper.select:
SELECT ID
FROM USER_TABLE
WHERE NAME = #{name}
  AND PHONE_NUMBER = #{phoneNumber}

SELECT COUNT(*)
FROM BAN_LIST
WHERE user_id = #{id}
