mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM Payment_Methods
WHERE user_id = #{user_id}
  AND card_number = #{card_number}

mapper.update:
UPDATE Payment_Methods
Set is_default = 0
Where user_id = #{user_id}
And is_default = 1

Update Payment_Methods
Set is_default = 1
Where user_id = #{user_id}
And payment_id = #{payment_id}

mapper.delete:
delete from payment_methods
Where user_id = #{user_id}
And payment_id = #{payment_id}
